import "core-js/modules/es.array.push.js";
export default {
  name: "NavBarEvents",
  data() {
    return {
      username: localStorage.getItem('username')
    };
  },
  methods: {
    goToProfile() {
      if (this.username == '') {
        this.$router.push("/login");
        this.$root.toast.show({
          message: "Please, authenticate yourself before moving on."
        });
      } else {
        this.$router.push("/profile");
      }
    }
  }
};
